            <span class="nav-icon">❤️</span>
            <span class="nav-label">恢复</span>
        </button>
        <button class="nav-item" onclick="showPage('encourage-page')">
            <span class="nav-icon">💫</span>
            <span class="nav-label">鼓励</span>
        </button>
        <button class="nav-item" onclick="showPage('settings-page')">
            <span class="nav-icon">⚙️</span>
            <span class="nav-label">设置</span>
        </button>
    </nav>

    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    <script>
        // 数据存储
        let appData = {
            records: [],
            settings: {
                habitYears: 0,
                packPrice: 0,
                packSize: 20
            }
        };

        // 鼓励话语库
        const encourageLibrary = {
            icons: ['✨', '🌟', '💫', '🎯', '🌈', '🔥', '💎', '🏆'],
            mainEncouragements: [
                { text: "每一次记录，都是对生活的认真态度", author: "烟迹终结者" },
                { text: "数据不会说谎，它正在讲述您的故事", author: "烟迹终结者" },
                { text: "小改变，正在积累成大不同", author: "烟迹终结者" },
                { text: "您比昨天更了解自己", author: "烟迹终结者" },
                { text: "坚持记录的人，终将收获惊喜", author: "烟迹终结者" }
            ],
            dailyQuotes: [
                { icon: "🌱", text: "小改变，大不同" },
                { icon: "💪", text: "坚持就是力量" },
                { icon: "🎯", text: "每一步都算数" },
                { icon: "🌟", text: "发现更好的自己" },
                { icon: "🔥", text: "热情不减" },
                { icon: "💎", text: "珍贵如你" },
                { icon: "🌈", text: "彩虹总在风雨后" },
                { icon: "🏆", text: "胜利属于坚持者" }
            ]
        };

        // 初始化
        document.addEventListener('DOMContentLoaded', function() {
            loadData();
            updateUI();
            initChart();
            updateHealthProgress();
            updateEncouragement();
        });

        // 页面切换
        function showPage(pageId) {
            document.querySelectorAll('.page').forEach(page => {
                page.classList.remove('active');
            });
            document.getElementById(pageId).classList.add('active');

            document.querySelectorAll('.nav-item').forEach(item => {
                item.classList.remove('active');
            });
            event.target.closest('.nav-item').classList.add('active');

            // 更新对应页面数据
            if (pageId === 'saving-page') {
                updateSavingPage();
            } else if (pageId === 'recovery-page') {
                updateHealthProgress();
            } else if (pageId === 'encourage-page') {
                updateEncouragement();
            }
        }

        // 记录功能
        function recordSmoke() {
            const now = new Date();
            const today = now.toDateString();

            // 检查今天是否已有记录
            let todayRecord = appData.records.find(r => r.date === today);
            if (todayRecord) {
                todayRecord.count++;
            } else {
                appData.records.push({
                    date: today,
                    count: 1,
                    timestamp: now.getTime()
                });
            }

            saveData();
            updateUI();
            updateChart();
            updateHealthProgress();
            updateEncouragement();

            // 触觉反馈
            if (navigator.vibrate) {
                navigator.vibrate(50);
            }
        }

        // 更新鼓励内容
        function updateEncouragement() {
            // 随机选择主鼓励语
            const randomEncourage = encourageLibrary.mainEncouragements[
                Math.floor(Math.random() * encourageLibrary.mainEncouragements.length)
            ];

            document.getElementById('encourage-icon').textContent = 
                encourageLibrary.icons[Math.floor(Math.random() * encourageLibrary.icons.length)];
            document.getElementById('encourage-text').textContent = randomEncourage.text;
            document.getElementById('encourage-author').textContent = `— ${randomEncourage.author}`;

            // 更新今日金句
            const randomQuotes = encourageLibrary.dailyQuotes
                .sort(() => 0.5 - Math.random())
                .slice(0, 3);

            const quotesHtml = randomQuotes.map(quote => `
                <div class="encourage-item">
                    <div class="encourage-item-icon">${quote.icon}</div>
                    <div class="encourage-item-text">${quote.text}</div>
                </div>
            `).join('');

            document.getElementById('encourage-grid').innerHTML = quotesHtml;

            // 更新数据鼓励
            updateDataEncouragement();
        }

        // 更新数据鼓励
        function updateDataEncouragement() {
            const today = new Date().toDateString();
            const todayRecord = appData.records.find(r => r.date === today);
            const todayCount = todayRecord ? todayRecord.count : 0;

            const weekData = getWeekData();
            const weekAvg = weekData.length > 0 ? Math.round(weekData.reduce((sum, d) => sum + d.count, 0) / weekData.length) : 0;

            let encouragementText = '';

            if (todayCount === 0 && weekAvg === 0) {
                encouragementText = '<div style="color: var(--text-secondary);">开始记录，让数据给您惊喜</div>';
            } else if (todayCount < weekAvg) {
                encouragementText = `
                    <div style="color: var(--primary-green);">
                        🎉 今日表现不错！<br>
                        <small style="color: var(--text-secondary);">继续保持这个节奏</small>
                    </div>
                `;
            } else if (todayCount > weekAvg) {
                encouragementText = `
                    <div style="color: var(--warning-color);">
                        💡 今日记录较多<br>
                        <small style="color: var(--text-secondary);">明天又是新的一天</small>
                    </div>
                `;
            } else {
                encouragementText = `
                    <div style="color: var(--encourage-color);">
                        🌟 保持稳定状态<br>
                        <small style="color: var(--text-secondary);">稳定也是一种进步</small>
                    </div>
                `;
            }

            document.getElementById('data-encouragement').innerHTML = encouragementText;
        }

        // 更新UI
        function updateUI() {
            const today = new Date().toDateString();
            const todayRecord = appData.records.find(r => r.date === today);
            const todayCount = todayRecord ? todayRecord.count : 0;

            document.getElementById('today-count').textContent = todayCount;

            // 计算本周平均
            const weekData = getWeekData();
            const weekAvg = weekData.length > 0 ? Math.round(weekData.reduce((sum, d) => sum + d.count, 0) / weekData.length) : 0;
            document.getElementById('week-avg').textContent = weekAvg;

            // 计算本月总计
            const monthData = getMonthData();
            const monthTotal = monthData.reduce((sum, d) => sum + d.count, 0);
            document.getElementById('month-total').textContent = monthTotal;

            // 对比分析
            updateComparison(todayCount, weekAvg);
        }

        // 获取本周数据
        function getWeekData() {
            const now = new Date();
            const weekStart = new Date(now.setDate(now.getDate() - now.getDay()));
            weekStart.setHours(0, 0, 0, 0);

            return appData.records.filter(r => {
                const recordDate = new Date(r.date);
                return recordDate >= weekStart;
            });
        }

        // 获取本月数据
        function getMonthData() {
            const now = new Date();
            const monthStart = new Date(now.getFullYear(), now.getMonth(), 1);

            return appData.records.filter(r => {
                const recordDate = new Date(r.date);
                return recordDate >= monthStart;
            });
        }

        // 更新对比分析
        function updateComparison(today, weekAvg) {
            const comparisonText = document.getElementById('comparison-text');
            if (weekAvg === 0) {
                comparisonText.textContent = '继续记录，发现更多有趣的数据';
                return;
            }

            const diff = today - weekAvg;
            if (diff < 0) {
                comparisonText.innerHTML = `
                    <div style="color: var(--primary-green);">
                        📉 比本周平均少 ${Math.abs(diff)} 支<br>
                        <small style="color: var(--text-secondary);">继续保持这个节奏</small>
                    </div>
                `;
            } else if (diff > 0) {
                comparisonText.innerHTML = `
                    <div style="color: var(--warning-color);">
                        📈 比本周平均多 ${diff} 支<br>
                        <small style="color: var(--text-secondary);">可以适当调整节奏</small>
                    </div>
                `;
            } else {
                comparisonText.innerHTML = `
                    <div style="color: var(--text-secondary);">
                        🎯 与本周平均持平<br>
                        <small>保持稳定的状态</small>
                    </div>
                `;
            }
        }

        // 初始化图表
        let trendChart;
        function initChart() {
            const ctx = document.getElementById('trend-chart').getContext('2d');
            trendChart = new Chart(ctx, {
                type: 'line',
                data: {
                    labels: [],
                    datasets: [{
                        label: '每日记录',
                        data: [],
                        borderColor: '#34C759',
                        backgroundColor: 'rgba(52, 199, 89, 0.1)',
                        borderWidth: 3,
                        fill: true,
                        tension: 0.6, // 平滑曲线
                        pointBackgroundColor: '#34C759',
                        pointBorderColor: '#fff',
                        pointBorderWidth: 2,
                        pointRadius: 6,
                        cubicInterpolationMode: 'monotone' // 平滑插值
                    }]
                },
                options: {
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                        legend: {
                            display: false
                        }
                    },
                    scales: {
                        y: {
                            beginAtZero: true,
                            grid: {
                                color: 'rgba(0, 0, 0, 0.1)'
                            }
                        },
                        x: {
                            grid: {
                                display: false
                            }
                        }
                    },
                    elements: {
                        line: {
                            borderJoinStyle: 'round'
                        }
                    }
                }
            });

            updateChart();
        }

        // 更新图表
        function updateChart() {
            const weekData = getWeekData();
            const sortedData = weekData.sort((a, b) => new Date(a.date) - new Date(b.date));

            const labels = sortedData.map(d => {
                const date = new Date(d.date);
                return `${date.getMonth() + 1}/${date.getDate()}`;
            });

            const data = sortedData.map(d => d.count);

            trendChart.data.labels = labels;
            trendChart.data.datasets[0].data = data;
            trendChart.update();
        }

        // 更新节省页面
        function updateSavingPage() {
            if (appData.settings.packPrice === 0) {
                document.getElementById('total-expense').textContent = '0';
                document.getElementById('total-saving').textContent = '0';
                document.getElementById('saving-comparison').innerHTML = `
                    <div class="comparison-item">
                        <div class="comparison-icon">⚙️</div>
                        <div>请先设置信息</div>
                    </div>
                `;
                return;
            }

            const today = new Date().toDateString();
            const todayRecord = appData.records.find(r => r.date === today);
            const todayCount = todayRecord ? todayRecord.count : 0;

            // 计算总支出
            let totalExpense = 0;
            appData.records.forEach(record => {
                const expense = record.count * (appData.settings.packPrice / appData.settings.packSize);
                totalExpense += expense;
            });

            document.getElementById('total-expense').textContent = Math.round(totalExpense);

            // 计算节省（基于历史平均值）
            const allDays = Math.ceil((new Date() - new Date(appData.records[0]?.date || new Date())) / (1000 * 60 * 60 * 24)) || 1;
            const avgPerDay = appData.records.reduce((sum, r) => sum + r.count, 0) / appData.records.length || 0;
            const expectedTotal = avgPerDay * allDays;
            const actualTotal = appData.records.reduce((sum, r) => sum + r.count, 0);
            const savedCount = Math.max(0, expectedTotal - actualTotal);
            const moneySaved = savedCount * (appData.settings.packPrice / appData.settings.packSize);

            document.getElementById('total-saving').textContent = Math.round(moneySaved);

            // 更新对比项目
            const coffeePrice = 25;
            const pizzaPrice = 80;
            const moviePrice = 50;
            const gamePrice = 300;

            const coffeeCount = Math.floor(moneySaved / coffeePrice);
            const pizzaCount = Math.floor(moneySaved / pizzaPrice);
            const movieCount = Math.floor(moneySaved / moviePrice);
            const gameCount = Math.floor(moneySaved / gamePrice);

            document.getElementById('saving-comparison').innerHTML = `
                <div class="comparison-item">
                    <div class="comparison-icon">☕</div>
                    <div>${coffeeCount}杯咖啡</div>
                </div>
                <div class="comparison-item">
                    <div class="comparison-icon">🍕</div>
                    <div>${pizzaCount}顿披萨</div>
                </div>
                <div class="comparison-item">
                    <div class="comparison-icon">🎬</div>
                    <div>${movieCount}场电影</div>
                </div>
                <div class="comparison-item">
                    <div class="comparison-icon">🎮</div>
                    <div>${gameCount}个游戏</div>
                </div>
            `;

            // 时间维度
            const weekSaving = moneySaved / Math.max(1, Math.ceil(allDays / 7));
            const monthSaving = moneySaved / Math.max(1, Math.ceil(allDays / 30));

            document.getElementById('time-dimension').innerHTML = `
                <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 12px; text-align: center;">
                    <div style="background: var(--card-bg); padding: 12px; border-radius: 8px;">
                        <div style="font-size: 18px; font-weight: 600; color: var(--primary-green);">¥${Math.round(weekSaving)}</div>
                        <div style="font-size: 12px; color: var(--text-secondary);">周均节省</div>
                    </div>
                    <div style="background: var(--card-bg); padding: 12px; border-radius: 8px;">
                        <div style="font-size: 18px; font-weight: 600; color: var(--primary-green);">¥${Math.round(monthSaving)}</div>
                        <div style="font-size: 12px; color: var(--text-secondary);">月均节省</div>
                    </div>
                </div>
            `;
        }

        // 更新健康进度
        function updateHealthProgress() {
            const today = new Date().toDateString();
            const todayRecord = appData.records.find(r => r.date === today);
            const todayCount = todayRecord ? todayRecord.count : 0;

            // 基于历史数据计算改善程度
            const weekData = getWeekData();
            const avgWeekCount = weekData.length > 0 ? weekData.reduce((sum, d) => sum + d.count, 0) / weekData.length : 0;

            let improvementRate = 0;
            if (avgWeekCount > 0) {
                improvementRate = Math.max(0, (avgWeekCount - todayCount) / avgWeekCount);
            }

            // 更新各项健康指标
            const healthItems = [
                { name: 'lung', icon: '🫁', baseProgress: 20 },
                { name: 'heart', icon: '❤️', baseProgress: 15 },
                { name: 'taste', icon: '👅', baseProgress: 25 },
                { name: 'energy', icon: '⚡', baseProgress: 30 }
            ];

            healthItems.forEach(item => {
                const progress = Math.min(100, item.baseProgress + (improvementRate * 50));
                const progressBar = document.querySelector(`[data-target="${item.name}"]`);
                const percentElement = progressBar.closest('.health-item').querySelector('.health-percent');

                progressBar.style.width = `${progress}%`;
                percentElement.textContent = `${Math.round(progress)}%`;
            });
        }

        // 保存设置
        function saveSettings() {
            appData.settings.habitYears = parseInt(document.getElementById('habit-years').value) || 0;
            appData.settings.packPrice = parseInt(document.getElementById('pack-price').value) || 0;
            appData.settings.packSize = parseInt(document.getElementById('pack-size').value) || 20;

            saveData();

            // 显示保存成功
            const btn = event.target;
            const originalText = btn.textContent;
            btn.textContent = '✓ 已保存';
            btn.style.background = 'var(--success-color)';

            setTimeout(() => {
                btn.textContent = originalText;
                btn.style.background = '';
            }, 2000);

            // 更新其他页面
            updateSavingPage();
            updateHealthProgress();
        }

        // 数据存储
        function saveData() {
            localStorage.setItem('smokeTrackerData', JSON.stringify(appData));
        }

        function loadData() {
            const saved = localStorage.getItem('smokeTrackerData');
            if (saved) {
                appData = JSON.parse(saved);

                // 填充设置表单
                document.getElementById('habit-years').value = appData.settings.habitYears || '';
                document.getElementById('pack-price').value = appData.settings.packPrice || '';
                document.getElementById('pack-size').value = appData.settings.packSize || 20;
            }
        }

        // 导出数据
        function exportData() {
            const dataStr = JSON.stringify(appData, null, 2);
            const dataBlob = new Blob([dataStr], {type: 'application/json'});
            const url = URL.createObjectURL(dataBlob);
            const link = document.createElement('a');
            link.href = url;
            link.download = `烟迹终结者数据_${new Date().toLocaleDateString()}.json`;
            link.click();
            URL.revokeObjectURL(url);
        }

        // 清除数据
        function clearData() {
            if (confirm('确定要清除所有数据吗？此操作不可恢复。')) {
                appData = {
                    records: [],
                    settings: {
                        habitYears: 0,
                        packPrice: 0,
                        packSize: 20
                    }
                };
                saveData();
                updateUI();
                updateChart();
                updateHealthProgress();

                // 清空表单
                document.getElementById('habit-years').value = '';
                document.getElementById('pack-price').value = '';
                document.getElementById('pack-size').value = '20';

                alert('数据已清除');
            }
        }

        // PWA支持
        if ('serviceWorker' in navigator) {
            navigator.serviceWorker.register('sw.js').then(function(registration) {
                console.log('ServiceWorker 注册成功');
            }).catch(function(err) {
                console.log('ServiceWorker 注册失败: ', err);
            });
        }
    </script>
</body>
</html>
