                recentDays.reduce((sum, d) => sum + d.count, 0) / recentDays.length : 0;

            // 计算相对于预测的节省
            const actualToday = appData.records.find(r => r.date === new Date().toDateString())?.count || 0;
            const predictedSaving = Math.max(0, (prediction.count - actualToday) * (appData.settings.packPrice / appData.settings.packSize));

