        // 智能预测算法
        function predictTomorrow() {
            if (appData.records.length < 3) {
                return { count: '--', confidence: 0 };
            }

            // 获取最近7天的数据
            const recentDays = getRecentDaysData(7);
            if (recentDays.length === 0) {
                return { count: '--', confidence: 0 };
            }

            // 计算多种预测方法
            const methods = {
                // 1. 简单平均法
                simpleAverage: () => {
                    const sum = recentDays.reduce((s, d) => s + d.count, 0);
                    return sum / recentDays.length;
                },

                // 2. 加权平均法（近期权重更高）
                weightedAverage: () => {
                    let weightedSum = 0;
                    let weightSum = 0;
                    recentDays.forEach((day, index) => {
                        const weight = index + 1;
                        weightedSum += day.count * weight;
                        weightSum += weight;
                    });
                    return weightedSum / weightSum;
                },

                // 3. 趋势法
                trendBased: () => {
                    if (recentDays.length < 2) return recentDays[0]?.count || 0;

                    let trend = 0;
                    for (let i = 1; i < recentDays.length; i++) {
                        trend += recentDays[i].count - recentDays[i-1].count;
                    }
                    trend = trend / (recentDays.length - 1);

                    const lastValue = recentDays[recentDays.length - 1].count;
                    return Math.max(0, lastValue + trend);
                },

                // 4. 周期性分析（周模式）
                weeklyPattern: () => {
                    const tomorrowDayOfWeek = (new Date().getDay() + 1) % 7;
                    const sameWeekDays = [];

                    for (let i = 0; i < appData.records.length; i++) {
                        const recordDate = new Date(appData.records[i].date);
                        if (recordDate.getDay() === tomorrowDayOfWeek) {
                            sameWeekDays.push(appData.records[i].count);
                        }
                    }

                    if (sameWeekDays.length > 0) {
                        return sameWeekDays.reduce((s, c) => s + c, 0) / sameWeekDays.length;
                    }

                    return recentDays[recentDays.length - 1]?.count || 0;
                }
            };

            // 综合多种预测结果
            const predictions = Object.values(methods).map(method => method());
            const avgPrediction = predictions.reduce((s, p) => s + p, 0) / predictions.length;

            // 计算置信度
            const variance = predictions.reduce((s, p) => s + Math.pow(p - avgPrediction, 2), 0) / predictions.length;
            const confidence = Math.max(0, 1 - (variance / (avgPrediction + 1)));

            return {
                count: Math.round(avgPrediction),
                confidence: confidence
            };
        }

        // 获取最近N天的数据
        function getRecentDaysData(days) {
            const now = new Date();
            const result = [];

            for (let i = 0; i < days; i++) {
                const date = new Date(now);
                date.setDate(date.getDate() - i);
                const dateStr = date.toDateString();

                const record = appData.records.find(r => r.date === dateStr);
                if (record) {
                    result.unshift(record);
                }
            }

            return result;
        }

